css:
.nuclide-instarn-toolbar,
.nuclide-instarn-toolbar-left {
  display: flex;
  flex: 1;
}
.nuclide-instarn-toolbar {
  justify-content: space-between;
}
.nuclide-instarn-toolbar-right {
  display: flex;
  margin-left: 10px;
}
.nuclide-instarn-builds-dropdown {
  width: 175px;
}
@keyframes nuclide-instarn-spin {
  100% {
    transform: rotate(360deg);
  }
}
.nuclide-instarn-refresh-button-icon {
  display: flex;
}
.nuclide-instarn-refresh-button.loading .nuclide-instarn-refresh-button-icon {
  animation: nuclide-instarn-spin 4s linear infinite;
}
.nuclide-instarn-refresh-button-icon::before {
  font-size: 1em;
  margin: 0;
  width: auto;
  height: auto;
  vertical-align: baseline;
}
.nuclide-instarn-metro-status-button {
  display: flex;
  font-size: 1.2em !important;
}
.nuclide-instarn-metro-status-button.stopped {
  color: #6f7a90;
}
.nuclide-instarn-metro-status-button.starting {
  color: #1492ff;
}
.nuclide-instarn-metro-status-button.starting:hover {
  color: #1492ff;
}
.nuclide-instarn-task-runner-icon {
  display: flex;
}
.nuclide-instarn-reload-nt-icon.icon-sync::before {
  font-size: 12px;
  margin: 0;
}
.nuclide-instarn-disconnect-nt-icon.icon-nuclicon-eject::before {
  font-size: 10px;
  margin: 0;
}
