css:
.connect-menu-breadcrumbs-container {
  display: flex;
  flex-direction: column;
  height: 2.5em;
  justify-content: center;
}
.connect-menu-breadcrumbs-button-container {
  display: flex;
}
.connect-menu-breadcrumbs-button-container .connect-menu-button-unselected {
  background-color: #0f1a27;
  background-image: none;
}
.connect-menu-breadcrumbs-button-container .btn {
  cursor: default;
}
.connect-menu-button-icon::before {
  top: 1px;
}
.connect-menu-container {
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 999;
}
.connect-menu {
  background: #132132;
  border: 1px solid #0b141e;
  border-radius: 4px;
  box-shadow: 0 6px 12px -2px rgba(0, 0, 0, 0.4);
  color: #99a7b8;
  padding: 4px;
  position: absolute;
}
.connect-menu-extra-actions-menu {
  margin-top: -3em;
  right: -256px;
  width: 250px;
}
.connect-menu-extra-actions-menu::after,
.connect-menu-extra-actions-menu::before {
  right: 100%;
  top: 0;
  border: solid transparent;
  content: " ";
  height: 0;
  width: 0;
  position: absolute;
  pointer-events: none;
}
.connect-menu-extra-actions-menu::after {
  border-color: rgba(0, 0, 0, 0);
  border-right-color: #132132;
  border-width: 6px;
  margin-top: 1em;
}
.connect-menu-extra-actions-menu::before {
  border-color: rgba(0, 0, 0, 0);
  border-right-color: #0b141e;
  border-width: 7px;
  margin-top: calc(1em - 1px);
}
.connect-menu-top-level-icon {
  margin-right: 4px;
}
.connect-menu-project-opener-heading-container {
  display: flex;
  flex-flow: row nowrap;
}
.connect-menu-project-opener-heading-title-container {
  display: flex;
  flex-flow: column nowrap;
}
.connect-menu-project-opener-heading-title {
  color: #99a7b8;
  font-weight: bold;
}
.connect-menu-project-opener-heading-subtitle {
  color: rgba(153, 167, 184, 0.6);
}
.connect-menu-clickable-row {
  cursor: default;
}
.connect-menu-clickable-row.selected {
  background-color: #4b84c9;
  color: #ffffff;
}
.connect-menu-clickable-row.selected .connect-menu-project-opener-heading-title,
.connect-menu-clickable-row.selected .connect-menu-project-opener-heading-subtitle {
  color: #ffffff;
}
.connect-menu-bordered-row {
  background: #1b3048;
  border: 1px solid #0b141e;
}
.connect-menu-project-root-row {
  position: relative;
  padding: 8px;
  margin-bottom: 1px;
}
.connect-menu-project-root-path {
  font-weight: bold;
}
.connect-menu-project-root-button-container {
  position: absolute;
  right: 0;
  color: rgba(153, 167, 184, 0.6);
}
.connect-menu-project-root-button-container:hover .icon {
  color: #222;
}
.connect-menu-project-root-button::before {
  right: 2px;
  top: 1px;
}
.connect-menu-project-root-extra-actions-icon-container {
  position: absolute;
  right: 0;
  color: rgba(153, 167, 184, 0.6);
}
.connect-menu-project-root-extra-actions-icon::before {
  right: 2px;
  top: 1px;
  transform: rotate(90deg);
}
.connect-menu-new-repo-section {
  margin-top: 3px;
  margin-bottom: 3px;
  padding-bottom: 8px;
}
.connect-menu-new-repo-heading {
  padding: 8px 8px 4px 8px;
}
.connect-new-repo-row {
  display: flex;
  flex-flow: row nowrap;
  padding: 6px 0px 6px 48px;
}
.connect-menu-add-project-row {
  padding: 8px;
  margin-top: 1px;
}
