css:
.nuclide-device-panel-container {
  flex-direction: column;
  padding: 5px 5px 0px 5px;
  flex: 0 0 100%;
  max-width: 100%;
  overflow-x: hidden;
}
.nuclide-device-panel-text-with-icon .icon {
  vertical-align: bottom;
}
.nuclide-device-panel-text-with-icon .icon:hover {
  color: #d5dbe2;
}
.nuclide-device-panel-text-with-icon .icon::before {
  vertical-align: bottom;
  margin-right: 0px;
}
.nuclide-device-panel-tasks-container {
  display: flex;
  flex-direction: column;
}
.nuclide-device-panel-disconnected-icon .icon {
  color: #cb2431;
}
.nuclide-device-panel-navigation-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-left: 0.5em;
  padding-right: 0.5em;
}
.nuclide-device-panel-host-selector {
  display: flex;
}
.nuclide-device-panel-debug-button {
  text-align: center;
  cursor: pointer;
}
.nuclide-device-panel-device-action-button {
  text-align: center;
  cursor: pointer;
}
.nuclide-device-panel-device-list-error {
  color: red;
  white-space: normal;
}
.nuclide-device-panel-process-table {
  font-size: 0.8em;
}
.nuclide-device-panel-process-table .icon:hover {
  color: #d5dbe2;
}
.nuclide-device-panel-process-table .icon::before {
  margin-right: 0px;
  vertical-align: top;
}
.nuclide-device-panel-app-info-button {
  float: right;
}
.nuclide-device-panel-app-info-button-edit {
  cursor: pointer;
}
