css:
.fb-package-center {
  height: 100%;
  width: 100%;
}
.fb-package-center .settings-view {
  height: 100%;
  width: 100%;
}
.fb-package-center-edit-package {
  padding: 10px;
}
.fb-package-center-edit-package .btn.btn-primary.fb-package-center-btn-busy {
  opacity: 1;
  background-image: linear-gradient(to right, transparent 50%, rgba(0, 0, 0, 0.15) 50%);
  background-size: 10px 100%;
  -webkit-animation: available-package-is-installing 0.5s linear infinite;
}
.fb-package-center-edit-package-path {
  display: flex;
}
.fb-package-center-edit-package-path-name {
  flex: 7;
  line-height: 25px;
}
.fb-package-center-edit-package-path-select {
  flex: 1;
  margin-right: 1em;
}
.fb-package-center-edit-package-name-group {
  display: flex;
  margin-bottom: 5px;
}
.fb-package-center-edit-package-name {
  flex: 5;
  margin-right: 1em;
}
.fb-package-center-edit-package-version {
  flex: 1;
}
.fb-package-center-edit-package-description atom-text-editor {
  background-color: #25282e;
  line-height: 25px;
  border-radius: 2px;
  padding-left: 5px;
  color: #ced2d4;
  font-size: 14px;
  min-height: 80px;
}
.fb-package-center-edit-package-description atom-text-editor .lines .line.cursor-line {
  background-color: transparent;
}
.fb-package-center-edit-package-buttons {
  display: flex;
  justify-content: flex-end;
}
.fb-package-center-edit-package-buttons button {
  width: 82px;
}
.fb-package-list-spinner {
  margin: auto;
}
.package-card-author-name {
  color: rgba(157, 165, 180, 0.6);
}
.my-packages-top-section {
  display: flex;
}
.my-packages-create-package {
  margin-left: auto;
  margin-bottom: 1em;
}
.packages-top-section {
  display: flex;
}
.packages-top-section-right-item {
  margin-left: auto;
  margin-bottom: 1em;
  line-height: 25px;
}
atom-panel.modal.fb-package-center-modal-dialog {
  width: 512px;
}
