css:
.nuclide-ui-full-width-progress-bar {
  width: 100%;
  height: 2px;
  position: absolute;
  bottom: 0;
  left: 0;
}
.nuclide-ui-full-width-progress-bar[hidden] {
  display: none;
}
.nuclide-ui-full-width-progress-bar.indeterminate {
  background-image: repeating-linear-gradient(90deg, #99cc99 0%, #99cc99 25%, transparent 25%, transparent 100%);
  background-size: 1000px 100%;
  animation: animate-indeterminate-bar 8s linear infinite;
}
.nuclide-ui-full-width-progress-bar.indeterminate:before {
  content: ' ';
  position: absolute;
  width: 100%;
  height: 100%;
  background-image: repeating-linear-gradient(90deg, rgba(153, 204, 153, 0.3) 0%, rgba(153, 204, 153, 0.3) 25%, transparent 25%, transparent 100%);
  background-size: 1800px 100%;
  animation: animate-indeterminate-bar-2 8s linear infinite;
}
@keyframes animate-indeterminate-bar {
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(1000px);
  }
}
@keyframes animate-indeterminate-bar-2 {
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(1800px);
  }
}
.nuclide-ui-full-width-progress-bar-bar {
  position: absolute;
  height: 100%;
  left: 0;
  width: 0;
  transition: width 0.3s ease-out;
  background-color: #99cc99;
}
