css:
.nuclide-task-runner-toolbar {
  flex: 1;
  overflow: hidden;
}
.nuclide-task-runner-toolbar.disabled {
  pointer-events: none;
}
.nuclide-task-runner-toolbar-contents {
  display: flex;
  flex: 1;
}
.nuclide-task-runner-task-runner-icon-wrapper {
  display: flex;
  align-items: center;
  height: 16px;
  width: 21px;
  margin-right: 5px;
  font-variant: small-caps;
  text-transform: lowercase;
}
.nuclide-task-runner-task-runner-icon-wrapper .icon-nuclicon-relay {
  display: flex;
}
.nuclide-task-runner-task-runner-icon-wrapper .icon-path-fill {
  fill: #7d868c;
}
.nuclide-task-runner-task-runner-button,
.nuclide-task-runner-task-runner-icon,
.nuclide-task-runner-task-runner-icon::before {
  display: flex;
  align-items: center;
}
.nuclide-task-runner-system-task-icon.nuclide-task-runner-system-task-icon::before {
  height: 11px;
  font-size: 11px;
  width: auto;
}
.nuclide-task-runner-tool-bar-button[hidden] {
  display: none;
}
.nuclide-task-runner-tool-bar-button[hidden] {
  display: none;
}
.nuclide-task-button {
  min-width: 35px;
}
.nuclide-task-runner-task-runner-button {
  display: flex;
  justify-content: space-between;
}
.nuclide-task-runner-task-runner-button .nuclide-dropdown-label-text-wrapper {
  min-width: 35px;
  margin-right: 8px;
}
