css:
.nuclide-phabricator-comments {
  font-family: 'Lucida Grande', 'Segoe UI', sans-serif;
  font-size: 11px;
  width: 100%;
  min-width: 800px;
}
.nuclide-phabricator-comments-toolbar {
  max-height: 25px;
}
.nuclide-phabricator-comments-showall {
  padding-top: 4px;
  padding-bottom: 4px;
  margin-left: 20px;
}
.nuclide-phabricator-comments-showall:hover {
  text-decoration: underline;
  cursor: pointer;
}
.nuclide-phabricator-comments-toolbar-buttons button {
  color: #484F5A;
}
.nuclide-phabricator-markup-editor-tools .nuclide-phabricator-markup-editor-upload-input {
  display: none;
}
.nuclide-phabricator-markup-editor-upload-label {
  height: 2em;
  width: 2em;
}
.nuclide-phabricator-markup-editor-button-group {
  width: 100%;
  flex-direction: row;
}
.nuclide-phabricator-markup-editor-button-group .btn.btn-xs {
  padding: 0;
  height: 2.2em;
  flex: 1;
}
.nuclide-phabricator-markup-editor-button-group * {
  font-size: 1.2em;
}
.nuclide-phabricator-markup-editor-button-group .icon::before {
  margin-right: 0;
}
.nuclide-phabricator-edit-preview-field {
  width: 100%;
  margin: 0;
  display: flex;
  flex-direction: column;
  flex-grow: 1;
}
.nuclide-phabricator-phabricator-markup-editor atom-text-editor {
  min-height: 8em;
  max-height: 300px;
  overflow: auto;
  padding-left: 5px;
}
.nuclide-phabricator-preview-markup {
  max-height: 300px;
  overflow: auto;
  margin: 5px 0;
}
.nuclide-phabricator-comments-rendered-markup {
  max-height: 500px;
  overflow: auto;
  margin: 5px 0;
}
.nuclide-phabricator-comment-wrapper {
  display: flex;
  flex-direction: row;
  align-items: flex-start;
}
.nuclide-phabricator-comment {
  background: #25282e;
  border-radius: 16px;
  position: relative;
  display: inline-block;
  margin-top: 2px;
  margin-bottom: 3px;
  margin-left: 4px;
  max-width: 500px;
}
.nuclide-phabricator-suggested-change-comment {
  margin-top: 10px;
  width: 450px;
}
.nuclide-phabricator-suggested-change-comment atom-text-editor .cursor {
  display: none;
}
.nuclide-phabricator-suggested-change-text {
  margin-bottom: 5px;
  font-size: 9.9px;
}
.nuclide-phabricator-create-suggestion-filecontents atom-text-editor {
  max-height: 500px;
  overflow: auto;
}
.nuclide-phabricator-suggested-change-button-group {
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  margin-top: 5px;
}
.nuclide-phabricator-comment-actions {
  white-space: nowrap;
  padding-left: 20px;
}
.nuclide-phabricator-comment-actions a {
  padding-right: 10px;
}
.nuclide-phabricator-comment-actions ::before {
  margin-right: 1px;
}
.nuclide-phabricator-comment-actions .icon-thumbsup::before {
  margin-right: 5px;
}
.nuclide-phabricator-comment-actions-row {
  display: flex;
  justify-content: space-between;
}
.nuclide-phabricator-comment-resolved {
  margin: 7px 5px 0 0;
}
.nuclide-phabricator-comment-author {
  margin-top: 5px;
}
.nuclide-phabricator-comment-content {
  padding: 6px 12px;
  word-wrap: break-word;
}
.nuclide-phabricator-comment-content p:last-child {
  margin-bottom: 0;
}
.nuclide-phabricator-comment-link,
.nuclide-phabricator-comment-resolved-label {
  padding: 5px;
}
.nuclide-phabricator-reply-form {
  overflow: auto;
  width: 476px;
  padding: 5px 0;
}
.nuclide-phabricator-edit-preview-loading {
  width: 100%;
  height: 80px;
  display: flex;
  justify-content: center;
  align-items: center;
}
.nuclide-phabricator-edit-preview-field {
  margin: 0;
  display: flex;
  flex-grow: 1;
}
atom-panel.full-border {
  border: 1px solid #1e2126;
}
.nuclide-phabricator-review-container {
  display: flex;
  flex: 1;
  flex-direction: column;
  width: 100%;
}
.nuclide-phabricator-comments-table-container {
  display: flex;
  flex-direction: column;
  overflow-y: hidden;
}
.nuclide-phabricator-comments-table-container .nuclide-ui-table {
  border-top-width: 0;
  border-left-width: 0;
  border-right-width: 0;
}
.nuclide-phabricator-comments-table-container .nuclide-ui-table td:last-child,
.nuclide-phabricator-comments-table-container .nuclide-ui-table th:last-child {
  border-right-width: 0;
}
.nuclide-phabricator-comment-accepted-gutter {
  border-left: 1px solid green;
}
.reviewer-added-lines-gutter-decoration {
  width: 0.4em;
  margin-left: 0.2em;
}
.reviewer-added-lines-gutter-decoration.added {
  z-index: 2;
  background: #6ac38a;
}
.reviewer-added-lines-gutter-decoration.deleted {
  z-index: 1;
  border-top: 0.4em solid #c45546;
  background: none;
}
.author-added-lines-gutter-decoration {
  z-index: 0;
  width: 0.8em;
  background: rgba(77, 164, 183, 0.5);
}
.nuclide-phabricator-comments-table {
  display: flex;
  overflow: hidden;
  flex-direction: column;
}
.nuclide-phabricator-comments-table-container-empty .nuclide-ui-table {
  border-bottom-width: 0;
}
.nuclide-phabricator-comments-empty-component {
  font-style: italic;
  padding: 1em;
  text-align: center;
}
.nuclide-phabricator-comments-dialog-container {
  padding: 10px;
}
.nuclide-phabricator-comments-dialog-contents {
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
  background: #25282e;
  display: flex;
  padding: 4px;
}
.nuclide-phabricator-comments-dialog-toolbar {
  padding-top: 10px;
  display: flex;
  justify-content: space-between;
}
.nuclide-phabricator-comment-list-toolbar {
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
  padding: 4px;
}
.nuclide-phabricator-comments-status-bar {
  cursor: pointer;
}
.nuclide-phabricator-comments-status-bar-count {
  color: #c69a6d;
}
.nuclide-phabricator-comments-refresh-status {
  width: 40px;
}
.nuclide-phabricator-suggested-change-overlay-container {
  display: flex;
  align-items: center;
  flex-direction: row;
  font-family: sans-serif;
  padding: 2px;
}
.nuclide-phabricator-suggested-change-selector-button {
  align-items: center;
}
.nuclide-phabricator-suggested-change-tool {
  display: flex;
  flex-direction: row;
}
atom-text-editor .nuclide-phabricator-comment-inline-highlight .region {
  background: rgba(255, 255, 255, 0.07);
}
.phabricator-markup-block-table {
  border-collapse: collapse;
}
.phabricator-markup-block-table td {
  padding: 0.5em;
  border: 1px solid rgba(64, 64, 64, 0.5);
}
.phabricator-markup-block-quote {
  font-size: 11px;
  font-family: 'Lucida Grande', 'Segoe UI', sans-serif;
  padding: 0 0 0 1em;
}
.phabricator-markup-block-thematic-break {
  width: 100%;
  border-top: 1px solid rgba(64, 64, 64, 0.5);
}
.phabricator-markup-entity-image,
.phabricator-markup-entity-video {
  height: auto;
  max-height: 250px;
  margin-bottom: 1em;
}
.phabricator-intern-macro-picker-preview-row {
  align-items: center;
  display: flex;
  height: 120px;
  justify-content: center;
  margin-bottom: 5px;
  width: 100%;
}
.phabricator-intern-macro-picker-results {
  overflow-x: auto;
  white-space: nowrap;
}
.phabricator-intern-macro-picker-preview {
  height: 120px;
  min-width: 60px;
  max-width: 240px;
}
atom-text-editor[mini].phabricator-intern-macro-picker-search-field {
  font-family: 'Lucida Grande', 'Segoe UI', sans-serif;
  font-size: 11px;
  line-height: 1.5em;
  margin-top: 2.5px;
  margin-bottom: 2.5px;
  width: 100%;
}
.phabricator-intern-macro-picker-preview-btn.btn {
  background: none;
  border: 0;
  margin-right: 5px;
  padding: 0;
}
.phabricator-intern-macro-picker-preview-btn.btn:last-child {
  margin-right: 0;
}
.phabricator-intern-macro-picker-loading-container {
  border: 0;
  height: 120px;
  width: 30px;
  margin: 0 1em;
  display: inline;
}
.phabricator-intern-macro-picker-loading-container .loading {
  display: inline-block;
  margin-top: 0.5em;
}
.phabricator-comments-reviewer-highlight {
  margin-left: 10px;
}
