css:
.fb-on-demand-connect-menu-container {
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 999;
}
.fb-on-demand-connect-menu {
  background: #2e3239;
  border: 1px solid rgba(255, 255, 255, 0.07);
  border-radius: 4px;
  box-shadow: 0 6px 12px -2px rgba(0, 0, 0, 0.4);
  color: #7d868c;
  padding: 4px;
  position: absolute;
}
.fb-on-demand-connect-menu-top-level-icon {
  margin-right: 4px;
}
.fb-on-demand-connect-menu-project-opener-heading-container {
  display: flex;
  flex-flow: row nowrap;
}
.fb-on-demand-connect-menu-project-opener-heading-title-container {
  display: flex;
  flex-flow: column nowrap;
}
.fb-on-demand-connect-menu-project-opener-heading-title {
  color: #7d868c;
  font-weight: bold;
}
.fb-on-demand-connect-menu-project-opener-heading-subtitle {
  color: #484F5A;
}
.fb-on-demand-connect-menu-clickable-row {
  cursor: pointer;
}
.fb-on-demand-connect-menu-clickable-row:hover,
.fb-on-demand-connect-menu-clickable-row:hover * {
  background-color: #25282e;
  color: #ffffff;
}
.fb-on-demand-connect-menu-bordered-row {
  background: #25282e;
  border: 1px solid rgba(255, 255, 255, 0.07);
}
.fb-on-demand-connect-menu-project-root-row {
  position: relative;
  padding: 8px;
  margin-bottom: 1px;
}
.fb-on-demand-connect-menu-project-root-path {
  font-weight: bold;
}
.fb-on-demand-connect-menu-project-root-remove-button {
  position: absolute;
  right: 0;
  color: #484F5A;
}
.fb-on-demand-connect-menu-project-root-remove-button:hover .icon {
  color: #222;
}
.fb-on-demand-connect-menu-new-repo-section {
  margin-top: 3px;
  margin-bottom: 3px;
  padding-bottom: 8px;
}
.fb-on-demand-connect-menu-new-repo-heading {
  padding: 8px 8px 4px 8px;
}
.fb-on-demand-connect-new-repo-row {
  display: flex;
  flex-flow: row nowrap;
  padding: 6px 0px 6px 48px;
}
.fb-on-demand-connect-menu-add-project-row {
  padding: 8px;
  margin-top: 1px;
}
