css:
.biggrep-container {
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
}
.biggrep-loading,
.biggrep-search-results-empty {
  align-items: center;
  background: #25282e;
  color: #484F5A;
  display: flex;
  flex-grow: 1;
  font-size: 3em;
  font-weight: bold;
  justify-content: center;
  padding: 1em;
  text-align: center;
  white-space: normal;
}
.biggrep-search-results-error {
  overflow-y: scroll;
  overflow-x: scroll;
  background: #25282e;
  display: flex;
  flex-grow: 1;
  padding: 1em;
  white-space: normal;
}
.biggrep-controls {
  background: #2e3239;
  border-bottom: 1px solid #1e2126;
  flex-shrink: 0;
  padding: 10px 0 0 10px;
  width: 100%;
}
.biggrep-controls-row {
  align-items: center;
  display: flex;
  flex-flow: row wrap;
  /* The first block fills the space. */
  /* The second blocks should line up. */
}
.biggrep-controls-row .biggrep-controls-block:first-child {
  flex: 1;
}
.biggrep-controls-row .biggrep-controls-block:not(:first-child) {
  min-width: 30em;
}
.biggrep-controls-block {
  align-items: center;
  display: flex;
  margin: 0 10px 10px 0;
}
.biggrep-controls-block atom-text-editor[mini] {
  min-width: 10em;
  width: 100%;
}
.biggrep-controls-block .select-list.popover-list {
  max-width: none;
  width: 100%;
}
.biggrep-controls-label,
.biggrep-controls-modifiers {
  margin-right: 10px;
  text-align: right;
}
.biggrep-controls-label,
.biggrep-controls-search-button,
.biggrep-controls-context-dropdown,
.biggrep-controls-modifiers,
.biggrep-controls-input {
  min-width: 6em;
}
.biggrep-controls-regex-button {
  font-family: Menlo, monospace;
}
.biggrep-controls-projects-dropdown {
  max-width: 24em;
}
.biggrep-search-results-wrapper {
  display: flex;
  flex-direction: column;
}
.biggrep-search-results {
  background: #2e3239;
  padding: 10px;
  flex-grow: 1;
  overflow-x: hidden;
  overflow-y: scroll;
}
.biggrep-search-results-summary {
  margin-bottom: 10px;
}
.biggrep-search-results-summary .warning {
  color: #c69a6d;
}
.biggrep-search-results-controls {
  background: #2e3239;
  position: relative;
}
.biggrep-search-results-controls-form {
  border-bottom: 1px solid #1e2126;
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-end;
  padding: 10px 0 0 10px;
}
.biggrep-search-results-controls-button {
  margin-left: 10px;
}
.biggrep-search-results-controls-toggle {
  margin: 0 10px 10px 0;
}
.biggrep-search-results-controls-toggle-hidden {
  border-top: none;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
  position: absolute;
  right: 10px;
}
.biggrep-search-results-filename {
  cursor: pointer;
  display: block;
  overflow-x: auto;
  white-space: nowrap;
  line-height: 25px;
}
.biggrep-search-results-filename:hover {
  color: #ced2d4;
}
.biggrep-search-results-filename .icon {
  text-align: center;
  vertical-align: middle;
}
.biggrep-search-results-snippet:last-child {
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
  margin-bottom: 8px;
}
.biggrep-search-results-snippet {
  border-radius: 2px;
  display: flex;
  border-top: 1px solid rgba(0, 0, 0, 0.5);
}
.biggrep-search-results-line-numbers {
  margin-left: 4em;
}
.biggrep-search-results-line-numbers,
.biggrep-search-results-code {
  display: inline-block;
  font-family: Menlo, Monaco, Consolas, monospace;
  line-height: 25px;
  white-space: pre;
}
.biggrep-search-results-line-numbers {
  color: #636d83;
  cursor: pointer;
  min-width: 3em;
  text-align: left;
  -webkit-user-select: none;
}
.biggrep-search-results-line-numbers > div:hover {
  color: #abb2bf;
}
.biggrep-search-results-code {
  color: #ced2d4;
  flex: 1;
  overflow-x: auto;
}
.biggrep-search-results-code .highlight-info {
  /* Copied from find-and-replace */
  box-shadow: inset 0 0 1px #ffffff;
  color: #ffffff;
  cursor: pointer;
}
.biggrep-search-results-code .highlight-info:hover {
  text-decoration: underline;
}
