css:
.datatip-overlay {
  background: #25282e;
  box-shadow: 0px 1px 4px 0px #000000;
  color: #abb2bf;
  font-family: Menlo, Monaco, Consolas, monospace;
  font-size: 11px;
  margin-top: -2px;
  position: relative;
  white-space: nowrap;
}
.datatip-dragging {
  cursor: -webkit-grabbing;
}
.datatip-pinned {
  cursor: -webkit-grab;
}
.datatip-container {
  background-color: #282c34;
  display: flex;
  position: relative;
  transition: background-color 0.15s ease;
}
.datatip-container:hover {
  background-color: #333842;
}
.datatip-container:not(:last-of-type) {
  border-bottom: 1px solid rgba(82, 139, 255, 0.1);
}
.datatip-content {
  flex-grow: 1;
}
.datatip-content .list-tree {
  cursor: inherit;
}
.datatip-pin-button {
  align-items: center;
  border-left: 1px solid rgba(82, 139, 255, 0.1);
  cursor: pointer;
  display: flex;
  line-height: 2;
  padding-bottom: 0.1em;
  padding-left: 0.5em;
  padding-right: 0.3em;
  padding-top: 0.0em;
}
.datatip-pin-button:hover {
  background-color: rgba(255, 255, 255, 0.05);
  color: #528bff;
}
.datatip-marked-text-editor .cursors {
  display: none;
}
atom-text-editor .datatip-highlight-region > .region {
  background: #3e4451;
}
atom-text-editor .datatip-highlight-region-active > .region {
  animation: RotatingBackground 1s ease-in-out infinite reverse;
  background: linear-gradient(90deg, #3e4451, #282c34, #3e4451, #282c34);
  background-size: 300% 100%;
}
