css:
.fb-on-demand-yubikey-dialog {
  padding: 0.5em;
  /* Needs to beat atom-panel.modal atom-text-editor[mini] */
}
.fb-on-demand-yubikey-dialog strong {
  color: #fff !important;
}
.fb-on-demand-yubikey-dialog .fb-on-demand-yubikey-dialog-bottom-row-container {
  background-color: #333;
  height: 100%;
  margin: -18px -15px -15px -15px;
}
.fb-on-demand-yubikey-dialog .fb-on-demand-yubikey-dialog-bottom-row {
  align-items: center;
  display: flex;
  justify-content: space-between;
  margin: 15px;
  padding-top: 18px;
}
.fb-on-demand-yubikey-dialog .fb-on-demand-yubikey-dialog-container .fb-on-demand-yubikey-input {
  margin-bottom: 0;
}
.fb-on-demand-yubikey-dialog-container > div + div {
  margin-top: 1em;
}
.fb-on-demand-yubikey-dialog-title {
  color: #fff;
  font-size: 1.75em;
  font-weight: bold;
  line-height: 1;
  margin-top: 1em;
  text-align: center;
}
.fb-on-demand-yubikey-input,
.fb-on-demand-yubikey-input-wrapper,
.fb-on-demand-yubikey-dialog-loader {
  flex-grow: 1;
}
.fb-on-demand-yubikey-input-wrapper {
  position: relative;
}
.fb-on-demand-yubikey-dialog-loader {
  left: 0;
  position: absolute;
  bottom: 0;
}
.fb-on-demand-yubikey-dialog-bottom-row-spacer {
  width: 1em;
}
.fb-on-demand-button-sizer {
  height: 0px;
  visibility: hidden;
  overflow: hidden;
}
.fb-on-demand-yubikey-dialog-description-wrapper {
  margin-bottom: 4em;
  overflow: hidden;
  position: relative;
  text-align: center;
}
.fb-on-demand-yubikey-dialog-loader {
  color: #fff;
  font-size: 1.25em;
}
/* needs to beat .fb-on-demand-yubikey-dialog-container > div + div */
html .fb-on-demand-yubikey-dialog-container > .fb-on-demand-yubikey-dialog-progress-bar-container {
  margin-top: -18px;
}
.fb-on-demand-beta-small,
.fb-on-demand-beta-medium {
  display: inline-block;
  margin-top: -0.15em;
  vertical-align: middle;
}
.fb-on-demand-beta-small,
.fb-on-demand-beta-medium {
  margin-left: 0.4em;
}
.fb-on-demand-beta-small {
  background: url(atom://nuclide/pkg/fb-on-demand/images/nuclide-beta-small.png);
  background-size: 22px 12px;
  height: 12px;
  width: 22px;
}
.fb-on-demand-beta-medium {
  background: url(atom://nuclide/pkg/fb-on-demand/images/nuclide-beta-medium.png);
  background-size: 36px 20px;
  height: 20px;
  width: 36px;
}
/* needs to beat atom-panel.modal.from-top */
html atom-panel.modal.fb-on-demand-spinner-modal,
html atom-panel.modal.fb-on-demand-spinner-modal > div {
  background: none;
  border: none;
  box-shadow: none;
  height: 100vh;
  top: 0;
}
atom-panel.modal.fb-on-demand-spinner-modal:before {
  content: none;
}
.theme-one-dark-ui .fb-on-demand-yubikey-dialog-bottom-row-container,
.theme-one-light-ui .fb-on-demand-yubikey-dialog-bottom-row-container {
  border-bottom-right-radius: 6px;
  border-bottom-left-radius: 6px;
}
.theme-one-dark-ui .fb-on-demand-connect-progress-bar,
.theme-one-light-ui .fb-on-demand-connect-progress-bar {
  left: -9px;
  width: calc(100% +  18px );
}
.theme-atom-light-ui .fb-on-demand-beta-small,
.theme-one-light-ui .fb-on-demand-beta-small {
  background: url(atom://nuclide/pkg/fb-on-demand/images/nuclide-beta-small-dark.png);
  background-size: 22px 12px;
}
.theme-atom-light-ui .fb-on-demand-beta-medium,
.theme-one-light-ui .fb-on-demand-beta-medium {
  background: url(atom://nuclide/pkg/fb-on-demand/images/nuclide-beta-medium-dark.png);
  background-size: 36px 20px;
}
.theme-atom-dark-ui atom-panel.modal.fb-on-demand-spinner-modal:after,
.theme-atom-light-ui atom-panel.modal.fb-on-demand-spinner-modal:after {
  content: "";
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: -1;
  background: #202123;
  opacity: 0.9;
  backface-visibility: hidden;
}
.theme-atom-dark-ui atom-panel.modal.fb-on-demand-yubikey-dialog-modal,
.theme-atom-light-ui atom-panel.modal.fb-on-demand-yubikey-dialog-modal {
  top: 50px;
}
/* Needs to beat .loading-spinner-large */
html .fb-on-demand-modal-spinner {
  font-size: 128px;
  height: 128px;
  left: 50%;
  position: absolute;
  top: 50%;
  transform: translate(-50%, -50%);
  width: 128px;
}
.fb-on-demand-connect-progress-bar {
  width: 100%;
  height: 2px;
  position: absolute;
  bottom: 0;
  left: 0;
  bottom: initial;
  margin-top: -2px;
  background-image: repeating-linear-gradient(90deg, #17ca65 0%, #17ca65 25%, transparent 25%, transparent 100%);
  background-size: 1000px 100%;
  animation: fb-on-demand-animate-progress-bar 8s linear infinite;
}
.fb-on-demand-connect-progress-bar:before {
  content: ' ';
  position: absolute;
  width: 100%;
  height: 100%;
  background-image: repeating-linear-gradient(90deg, rgba(23, 202, 101, 0.3) 0%, rgba(23, 202, 101, 0.3) 25%, transparent 25%, transparent 100%);
  background-size: 1800px 100%;
  animation: fb-on-demand-animate-progress-bar-2 8s linear infinite;
}
@keyframes fb-on-demand-animate-progress-bar {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 1000px 0;
  }
}
@keyframes fb-on-demand-animate-progress-bar-2 {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 1800px 0;
  }
}
