css:
.fb-interactive-smartlog-container {
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
  -webkit-user-select: none;
}
.fb-interactive-smartlog-failed-to-load-container {
  display: flex;
  flex-direction: column;
  align-items: center;
}
.fb-interactive-smartlog-main-pane {
  flex: 3;
  padding: 0 0 10px 10px;
  overflow: auto;
  position: relative;
}
atom-panel-container .fb-interactive-smartlog-main-pane {
  background-color: #1e1e1e;
}
.fb-interactive-smartlog-root-group {
  transition: opacity 0.2s ease-out;
}
.fb-interactive-smartlog-root-group-loading {
  pointer-events: none;
  opacity: 0.3;
}
.fb-interactive-smartlog-main-pane-loading-indicator {
  align-items: center;
  display: flex;
  height: 100%;
  justify-content: center;
  position: absolute;
  top: 0;
  width: 100%;
}
.fb-interactive-smartlog-quick-input-dialog-toolbar {
  display: flex;
  justify-content: space-between;
}
.fb-interactive-smartlog-action-pane {
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
  padding: 10px 10px 0 10px;
}
.fb-interactive-smartlog-revision-group {
  margin-bottom: 30px;
  margin-left: 20px;
  margin-top: 10px;
  position: relative;
}
.fb-interactive-smartlog-revision-group:before {
  content: " ";
  border-left: 2px solid #747369;
  position: absolute;
  bottom: 5px;
  top: 5px;
  left: -10px;
}
.fb-interactive-smartlog-branch-indicator {
  position: absolute;
  bottom: -20px;
  left: -30px;
  margin-bottom: -5px;
}
.fb-interactive-smartlog-branch-indicator path {
  stroke: #747369;
}
.fb-interactive-smartlog-revision {
  transition: opacity 0.3s ease-out;
}
.fb-interactive-smartlog-revision-muted {
  opacity: 0.5;
}
.fb-interactive-smartlog-revision-actions {
  display: inline-block;
  position: relative;
  top: -1px;
  border: 1px solid transparent;
}
.fb-interactive-smartlog-revision-action-ephemeral {
  visibility: hidden;
}
.fb-interactive-smartlog-revision-expanded .fb-interactive-smartlog-revision-action-ephemeral {
  visibility: visible;
}
.fb-interactive-smartlog-revision-stripping .fb-interactive-smartlog-revision-title-text {
  cursor: not-allowed;
  opacity: 0.5;
  text-decoration: line-through;
}
.fb-interactive-smartlog-revision-summary {
  min-height: 2em;
}
.fb-interactive-smartlog-top-revision-icon {
  margin-left: -15px;
  background: #262626;
  position: relative;
}
.fb-interactive-smartlog-revision-list-action {
  margin-left: 2px;
}
.fb-interactive-smartlog-revision-avatar {
  margin-left: -19px;
  position: relative;
  border: 2px solid rgba(232, 230, 223, 0.6);
  transition: box-shadow 2s ease-in, border-color 2s ease-in, border-width 2s ease-in;
}
.fb-interactive-smartlog-revision:not(:hover) .fb-interactive-smartlog-revision-avatar-compacted:not(.fb-interactive-smartlog-revision-avatar-is-head) {
  border-width: 1px;
  margin-left: -15px;
  margin-right: 4px;
  margin-top: 4px;
  position: relative;
  z-index: 1;
}
.fb-interactive-smartlog-revision:not(:hover) .fb-interactive-smartlog-revision-avatar-compacted:not(.fb-interactive-smartlog-revision-avatar-is-head) .fb-interactive-smartlog-revision-title .nuclide-ui-fb-avatar-image {
  -webkit-filter: blur(1.5px) brightness(1.5);
}
.fb-interactive-smartlog-revision-avatar-anonymized {
  border: none;
}
.fb-interactive-smartlog-revision-avatar-anonymized.nuclide-ui-fb-avatar {
  background-color: #747369;
}
.fb-interactive-smartlog-revision-avatar-anonymized.fb-interactive-smartlog-revision-avatar-is-head {
  background-color: #6699cc;
}
.fb-interactive-smartlog-revision-avatar-is-head {
  border-color: #6699cc;
}
.fb-interactive-smartlog-head-indicator {
  position: relative;
}
.fb-interactive-smartlog-head-indicator::before {
  border-left: 2px solid #6699cc;
  content: " ";
  display: block;
  height: 100%;
  left: -10px;
  position: absolute;
}
.fb-interactive-smartlog-uncommittedChanges-you-are-here {
  margin-bottom: 4px;
  margin-left: -6px;
  margin-top: 10px;
}
.fb-interactive-smartlog-revision-you-are-here {
  margin-bottom: 6px;
  margin-left: -20px;
  position: relative;
}
.fb-interactive-smartlog-revision-you-are-here::before {
  border-left: 2px solid #6699cc;
  bottom: -10px;
  content: " ";
  display: block;
  height: 10px;
  left: 10px;
  position: absolute;
}
.fb-interactive-smartlog-you-are-here-label {
  background-color: #6699cc;
  border-radius: 10px;
  color: #e8e6df;
  display: inline-block;
  font-weight: bold;
  padding: 2px 8px;
}
.fb-interactive-smartlog-you-are-here-label .loading-spinner-tiny {
  display: inline-block;
  margin-left: 2px;
  margin-right: -4px;
  top: -1px;
  vertical-align: bottom;
}
.fb-interactive-smartlog-you-are-here-inline {
  font-size: 10px;
  margin-right: 4px;
}
.fb-interactive-smartlog-revision:hover .fb-interactive-smartlog-revision-title .nuclide-ui-fb-avatar {
  border-radius: 10px!important;
  height: 20px!important;
  width: 20px!important;
}
.fb-interactive-smartlog-revision-expansion-chevron {
  color: #747369;
  cursor: pointer;
  display: inline-block;
  margin-left: 0.25em;
  transition: opacity 0.1s ease-in-out;
  width: 1em;
  opacity: 0;
  padding-right: 1000px;
}
.fb-interactive-smartlog-revision-expansion-chevron:hover {
  color: #e8e6df;
}
.fb-interactive-smartlog-only-revision-selected.fb-interactive-smartlog-revision-not-being-dragged .fb-interactive-smartlog-revision-expansion-chevron,
.fb-interactive-smartlog-revision-not-being-dragged .fb-interactive-smartlog-revision-summary:hover .fb-interactive-smartlog-revision-expansion-chevron {
  opacity: 1;
}
.fb-interactive-smartlog-revision-not-being-dragged .fb-interactive-smartlog-revision-expansion-chevron-expanded {
  opacity: 1;
}
.fb-interactive-smartlog-revision {
  white-space: nowrap;
  cursor: pointer;
}
.fb-interactive-smartlog-revision:hover .fb-interactive-smartlog-revision-not-being-dragged,
.fb-interactive-smartlog-revision .fb-interactive-smartlog-only-revision-selected.fb-interactive-smartlog-revision-not-being-dragged {
  color: #e8e6df;
}
.fb-interactive-smartlog-revision:hover .fb-interactive-smartlog-revision-not-being-dragged .fb-interactive-smartlog-revision-action-ephemeral,
.fb-interactive-smartlog-revision .fb-interactive-smartlog-only-revision-selected.fb-interactive-smartlog-revision-not-being-dragged .fb-interactive-smartlog-revision-action-ephemeral {
  visibility: visible;
}
.fb-interactive-smartlog-missing-parent-indicator {
  border-left: 2px dotted #262626;
  height: 10px;
  margin-bottom: 5px;
  margin-left: -10px;
  margin-top: 5px;
  position: relative;
}
.fb-interactive-smartlog-bookmark-spacer {
  margin-right: 5px;
}
.fb-interactive-smartlog-revision-details-content {
  background-color: #1e1e1e;
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
  color: #747369;
  cursor: default;
  display: inline-flex;
  flex-direction: column;
  margin: 10px 10px 20px 5px;
  overflow: auto;
  padding: 10px;
  white-space: normal;
  width: 60em;
  -webkit-user-select: initial;
}
.fb-interactive-smartlog-revision-details-content > pre {
  padding: 0;
}
.fb-interactive-smartlog-revision-details-content h2 {
  color: #e8e6df;
  margin-bottom: 10px;
  margin-top: 0;
  line-height: 1.5;
}
.fb-interactive-smartlog-revision-details-content .nuclide-ui-fb-avatar-image {
  background-image: linear-gradient(45deg, #1e1e1e, #1e1e1e);
}
.fb-interactive-smartlog-revision-details-container {
  display: inline-flex;
  flex-direction: column;
  margin-right: 30px;
  margin-top: 10px;
}
.fb-interactive-smartlog-revision-details-label {
  margin-bottom: 5px;
}
.fb-interactive-smartlog-revision-details-created-by {
  margin-bottom: 10px;
}
.fb-interactive-smartlog-revision-details-markdown {
  margin-bottom: 10px;
}
.fb-interactive-smartlog-revision-details-markdown p:last-child {
  margin-bottom: 0;
}
.fb-interactive-smartlog-revision-details-markdown img {
  max-width: 40em;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-container {
  min-width: 12em;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-content {
  background-color: initial;
  border: none;
  border-radius: none;
  margin: 0 10px 0 5px;
  width: 100%;
  height: 100%;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-editing-container {
  margin: 10px 0 10px 0;
  width: 100%;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-label {
  color: #7f7e73;
  font-weight: bold;
  user-select: none;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-label .icon {
  cursor: pointer;
  opacity: 0;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-label .icon:before {
  padding: 0 8px;
  font-size: 12px;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-label .fb-interactive-smartlog-revision-details-label-spinner {
  display: inline-block;
  margin-left: 10px;
  vertical-align: bottom;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-empty-field-label {
  opacity: 0.8;
  cursor: pointer;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-empty-field-label .icon:before {
  padding: 0 8px;
  font-size: 12px;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-empty-field-label:hover {
  color: #e8e6df;
  opacity: 1;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-title {
  color: #e8e6df;
  font-size: 1.5em;
  font-weight: 500;
  line-height: 1.5;
  margin-bottom: 10px;
  margin-top: 0;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-title .icon:before {
  font-size: 16px;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-fold-preview-label {
  color: #e8e6df;
  background-color: #99cc99;
  border-radius: 10px;
  display: inline-block;
  font-weight: bold;
  font-size: 10px;
  padding: 2px 8px;
  margin-right: 4px;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-container:hover .fb-interactive-smartlog-revision-details-label .icon {
  opacity: 1;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-top-toolbar {
  border-top: 1px solid rgba(0, 0, 0, 0.5);
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-raw-markdown {
  white-space: pre-wrap;
  margin-bottom: 1.5em;
}
.fb-interactive-smartlog-commit-info-view .phabricator-markup-block {
  margin-bottom: 1.5em;
}
.fb-interactive-smartlog-commit-info-view .fb-interactive-smartlog-revision-details-changed-files {
  margin: 10px 0;
}
.fb-interactive-smartlog-revision-details-successor-link {
  cursor: pointer;
  color: #e8e6df;
}
.fb-interactive-smartlog-revision-details-successor-link:hover {
  text-decoration: underline;
}
.tokenized-input-container {
  display: flex;
  flex: 1;
  flex-wrap: wrap;
}
.tokenized-input-container atom-text-editor {
  margin-top: 2px;
  min-width: 25%;
}
.tokenized-input-removable-tag {
  padding-top: 2px;
  font-size: 14px;
  margin-right: 4px;
}
.fb-interactive-smartlog-revision-public {
  color: #747369;
}
.fb-interactive-smartlog-revision-date {
  color: #747369;
  margin-left: 0.5em;
  margin-right: 0.5em;
}
.fb-interactive-smartlog-comments-stats {
  color: #747369;
  margin-left: 0.5em;
  margin-right: 0.5em;
  position: relative;
  top: 1px;
}
.fb-interactive-smartlog-comments-stats-inactive {
  cursor: not-allowed;
}
.fb-interactive-smartlog-task-icon {
  position: relative;
  top: 1px;
}
.fb-interactive-smartlog-unsaved-changes-indicator::before {
  color: #6699cc;
  font-size: 14px;
  margin-right: 0;
  margin-left: 4px;
  width: 4px;
}
.fb-interactive-smartlog-revision-click-to-select {
  padding: 3px 0;
}
.fb-interactive-smartlog-revision-title {
  transition: all 2s ease-in;
}
.fb-interactive-smartlog-revision-highlighted .fb-interactive-smartlog-revision-title {
  color: #6699cc;
  text-shadow: 0 0 10px #6699cc;
  transition: none;
}
.fb-interactive-smartlog-revision-highlighted .fb-interactive-smartlog-revision-avatar {
  border-width: 3px;
  border-color: #6699cc;
  box-shadow: 0 0 10px #6699cc;
  transition: none;
}
.fb-interactive-smartlog-revision-selected {
  position: relative;
  z-index: 0;
}
.fb-interactive-smartlog-revision-selected .fb-interactive-smartlog-revision-title {
  color: #e8e6df;
}
.fb-interactive-smartlog-revision-selected::before {
  background-color: #1e1e1e;
  content: " ";
  height: 100%;
  margin-left: -100%;
  margin-top: -3px;
  opacity: 0.5;
  position: absolute;
  width: 200%;
  z-index: -1;
}
.fb-interactive-smartlog-revision-selected .fb-interactive-smartlog-revision-avatar {
  border-width: 3px;
  border-color: #e8e6df;
}
.fb-interactive-smartlog-revision-selected .fb-interactive-smartlog-revision-title .nuclide-ui-fb-avatar-image {
  -webkit-filter: brightness(1.5);
}
.fb-interactive-smartlog-clickable-icon {
  cursor: pointer;
}
.fb-interactive-smartlog-clickable-icon.icon:hover {
  color: #e8e6df;
}
.fb-interactive-smartlog-clickable-icon.icon {
  color: #747369;
}
/* uncommitted changes, merge conflicts */
.fb-interactive-smartlog-uncommittedChanges,
.fb-interactive-smartlog-mergeConflicts {
  border-left: 2px dotted #262626;
  margin-left: -10px;
  position: relative;
  padding: 0 10px 10px 10px;
}
.fb-interactive-smartlog-uncommittedChanges-actionRow,
.fb-interactive-smartlog-mergeConflicts-actionRow {
  margin-left: -18px;
  margin-top: -5px;
}
.fb-interactive-smartlog-uncommittedChanges-title,
.fb-interactive-smartlog-mergeConflicts-title {
  color: #f99157;
  display: inline-block;
  vertical-align: middle;
  margin-left: 1em;
}
.fb-interactive-smartlog-uncommittedChangesList-title,
.fb-interactive-smartlog-mergeConflictsList-title {
  display: inline-block;
  margin-left: 20px;
  margin-top: 5px;
}
.fb-interactive-smartlog-uncommittedChangesList-title > div,
.fb-interactive-smartlog-mergeConflictsList-title > div {
  margin-bottom: 5px;
}
.fb-interactive-smartlog-uncommittedChanges-toggle-select {
  padding-left: 15px;
}
.fb-interactive-smartlog-uncommittedChangesList,
.fb-interactive-smartlog-mergeConflictsList {
  padding: 0 0 0 4px;
}
.fb-interactive-smartlog-uncommittedChangesList .nuclide-changed-file .nuclide-changed-file-name,
.fb-interactive-smartlog-mergeConflictsList .nuclide-changed-file .nuclide-changed-file-name {
  flex-grow: 0;
}
.fb-interactive-smartlog-uncommittedChangesList .nuclide-changed-file .nuclide-changed-file-actions,
.fb-interactive-smartlog-mergeConflictsList .nuclide-changed-file .nuclide-changed-file-actions {
  margin-left: 0;
}
.fb-interactive-smartlog-uncommittedChangesList-discarding {
  opacity: 0.25;
  pointer-events: none;
}
.fb-interactive-smartlog-uncommittedChangesList-discarding-message {
  vertical-align: text-bottom;
}
.fb-interactive-smartlog-uncommittedChangesList-discarding-spinner {
  display: inline-block;
  margin: 0 0.5em;
  top: 4px;
}
.fb-interactive-smartlog-uncommittedChangesList-discarding-spinner.loading-spinner-tiny {
  display: inline-block;
}
.fb-interactive-smartlog-quick-commit-form {
  display: inline-flex;
}
.fb-interactive-smartlog-quick-commit-form-input {
  width: 20em;
}
.fb-interactive-smartlog-quick-commit-form-commit-button {
  margin-top: 1px;
}
.fb-interactive-smartlog-static-section {
  display: flex;
  flex-direction: column;
}
.fb-interactive-smartlog-documentation {
  background-color: #262626;
  bottom: 0;
  position: absolute;
  width: 100%;
  z-index: 1;
}
.fb-interactive-smartlog-documentation .nuclide-resize-sensitive-container-wrapper {
  border-top: 1px solid rgba(0, 0, 0, 0.5);
}
.fb-interactive-smartlog-actions {
  align-items: flex-start;
  display: flex;
  flex-wrap: wrap;
}
.fb-interactive-smartlog-actions-full-width-progress-bar {
  display: flex;
  position: relative;
}
.fb-interactive-smartlog-padded-left {
  padding-left: 10px;
}
.fb-interactive-smartlog-action {
  margin-right: 10px;
  margin-bottom: 10px;
}
.fb-interactive-smartlog-available-repositories-component {
  display: flex;
  align-items: baseline;
}
.fb-interactive-smartlog-revision-details-link {
  margin-bottom: 5px;
}
a.fb-interactive-smartlog-revision-details-link:hover {
  text-decoration: none;
}
.fb-interactive-smartlog-revision-details-link:hover .fb-interactive-smartlog-revision-details-link-label {
  text-decoration: underline;
}
/* Command documentation panel */
.fb-interactive-smartlog-command {
  align-items: baseline;
  display: flex;
  flex-flow: row wrap;
  padding: 5px 10px 0 10px;
}
.fb-interactive-smartlog-command-row {
  align-items: baseline;
  display: flex;
}
.fb-interactive-smartlog-command-prompt {
  margin-bottom: 5px;
  padding: 0 5px;
}
.fb-interactive-smartlog-command-label {
  margin-bottom: 5px;
  padding: 0 5px;
}
.fb-interactive-smartlog-command-equivalent,
.fb-interactive-smartlog-progress-pane-command {
  background-color: #1e1e1e;
  border-radius: 2px;
  font-family: Menlo, Monaco, Consolas, "Courier New", monospace;
  margin-right: 10px;
  padding: 0.15em 0.5em;
}
.fb-interactive-smartlog-command-documentation-row {
  margin-bottom: 10px;
  margin-top: 5px;
  padding-right: 5px;
}
.fb-interactive-smartlog-command-documentation-command-row {
  display: flex;
  flex-direction: row-reverse;
}
.fb-interactive-smartlog-content-container {
  display: flex;
  flex-direction: column;
  flex: 1;
}
/* Build Status */
.nuclide-interactive-smartlog-build-status {
  align-items: flex-start;
  display: flex;
  flex-direction: column;
  margin: 10px 0;
}
.nuclide-interactive-smartlog-build-status-title {
  margin-bottom: 10px;
}
.nuclide-interactive-smartlog-build-status-metadata {
  align-items: baseline;
  display: flex;
  min-height: 2em;
}
.nuclide-interactive-smartlog-build-status-failure {
  color: #f2777a;
  vertical-align: middle;
}
.nuclide-interactive-smartlog-build-status-success {
  color: #99cc99;
}
.nuclide-interactive-smartlog-build-status-warning {
  color: #f99157;
}
.nuclide-interactive-smartlog-build-status-time-remaining {
  font-weight: normal;
  color: #747369;
  margin: 0 10px;
}
/* suda toolbar button with modified files count */
.fb-interactive-smartlog-toolbar-button.tool-bar-btn::after {
  content: attr(data-count);
  font-size: 0.8em;
  margin-top: 13px;
  position: absolute;
  text-shadow: 0 0px 2px rgba(0, 0, 0, 0.5);
}
.fb-interactive-smartlog-toolbar-button.tool-bar-btn.max-count::after {
  margin-left: -4px;
}
.fb-interactive-smartlog-toolbar-button.tool-bar-btn.positive-count {
  color: #f99157;
}
.fb-interactive-smartlog-toolbar-button.tool-bar-btn.positive-count:hover {
  color: #f99157;
}
.fb-interactive-smartlog-revision-drag-preview .fb-interactive-smartlog-revision-group:before,
.fb-interactive-smartlog-revision-drag-preview .fb-interactive-smartlog-revision-avatar {
  border-color: #99cc99;
}
.fb-interactive-smartlog-revision-drag-preview .fb-interactive-smartlog-branch-indicator path {
  stroke: #99cc99;
}
.fb-interactive-smartlog-revision-drag-preview .fb-interactive-smartlog-revision-title {
  color: #99cc99;
}
.fb-interactive-smartlog-revision-drag-preview .fb-interactive-smartlog-revision-actions {
  margin-bottom: -10px;
  margin-left: 10px;
}
.fb-interactive-smartlog-revision-preview-display .fb-interactive-smartlog-revision-group:before,
.fb-interactive-smartlog-revision-preview-display .fb-interactive-smartlog-revision-avatar {
  border-color: #99cc99;
}
.fb-interactive-smartlog-revision-preview-display .fb-interactive-smartlog-revision-title {
  color: #99cc99;
}
/* jellyfish-related components */
.fb-interactive-smartlog-jellyfish-revision-status-container {
  display: flex;
  align-items: center;
  margin-left: 10px;
