css:
.theme-atom-dark-ui atom-panel.modal.fb-on-demand-migrate-container,
.theme-atom-light-ui atom-panel.modal.fb-on-demand-migrate-container {
  top: 50px;
}
@media screen and (max-width: 1599px) {
  .theme-atom-dark-ui atom-panel.modal.fb-on-demand-migrate-container,
  .theme-atom-light-ui atom-panel.modal.fb-on-demand-migrate-container {
    left: 0;
    right: 0;
    margin: 0;
    width: 90%;
    max-width: none;
  }
}
@media screen and (min-width: 1600px) {
  .theme-atom-dark-ui atom-panel.modal.fb-on-demand-migrate-container,
  .theme-atom-light-ui atom-panel.modal.fb-on-demand-migrate-container {
    width: 1550px;
    left: 50%;
    margin-left: -775px;
    /* half the width to go with the left 50% */
    max-width: none;
  }
}
.theme-one-dark-ui atom-panel.modal.fb-on-demand-migrate-container,
.theme-one-light-ui atom-panel.modal.fb-on-demand-migrate-container {
  max-width: 1550px;
  width: 90%;
}
.fb-on-demand-migrate-container {
  width: 368px;
  margin: 0;
  background-color: #1d1f21;
  height: 200px;
}
.fb-on-demand-migrate-container {
  padding: 10px;
}
.fb-on-demand-migrate-close {
  position: absolute;
  right: 0px;
  z-index: 1;
  cursor: pointer;
}
.fb-on-demand-migrate {
  position: relative;
  background-color: #1d1f21;
}
.fb-on-demand-migrate-spinner {
  margin: 30px auto;
  background-color: #1d1f21;
}
.fb-on-demand-backup-options-body {
  margin: 0 auto;
  width: 50%;
  left: 25%;
  position: absolute;
}
.backup-options-prompt {
  padding-left: 10px;
  font-size: 16px;
}
.on-demand-backup-menu {
  background: #1d1f21;
  border-radius: 4px;
  color: #a3a8ae;
  padding-top: 10px;
}
.on-demand-backup-selectable-item {
  cursor: pointer;
}
.on-demand-backup-selectable-item:hover,
.on-demand-backup-selectable-item:hover * {
  background-color: #4a89c7;
  color: #ffffff;
}
.on-demand-backup-menu-item-row {
  cursor: default;
}
.on-demand-backup-menu-item-row.selected {
  background-color: #4a89c7;
  color: #ffffff;
}
.on-demand-backup-menu-bordered-row {
  background: #2b2e31;
  border: 1px solid #111314;
}
.backup-description {
  padding: 8px;
}
.on-demand-backup-menu-item-row {
  padding: 8px;
  margin-top: 3px;
}
.fb-on-demand-backup-progress-body {
  padding-top: 30px;
  margin: 0 auto;
  width: 90%;
}
.fb-on-demand-backup-progress-message {
  padding-top: 10px;
  margin: 0 auto;
  padding-bottom: 5px;
}
.fb-on-demand-backup-progress-bar {
  margin: 0 auto;
  position: absolute;
  width: 90%;
  left: 5%;
}
.fb-on-demand-migrate-modal-spinner {
  margin: 50px auto;
}
/* restore */
.fb-on-demand-migrate-restore-loading-overlay {
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.8);
  /* Black background with opacity */
  z-index: 9998;
  position: absolute;
}
.fb-on-demand-migrate-restore {
  top: 50%;
  left: 50%;
  position: absolute;
  transform: translate(-50%, -50%);
}
.fb-on-demand-migrate-restore-message {
  width: 239px;
  top: 50%;
  left: 50%;
  color: #4a89c7;
  text-align: center;
  line-height: 1.6;
  margin-bottom: 170px;
  position: relative;
  transform: translate(-50%, -50%);
}
/* Deprecated instance overlay */
.fb-on-demand-migrate-deprecated-overlay {
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(255, 165, 0, 0.09);
  /* Orange background with low opacity */
  z-index: 10000;
  position: absolute;
  pointer-events: none;
}
/* Migration Error Dialog */
.fb-on-demand-migrate-error-dialog-bottom-row-container {
  background-color: #161719;
  height: 100%;
  margin: -20px -9px -9px -9px;
  border-bottom-right-radius: 6px;
  border-bottom-left-radius: 6px;
}
.fb-on-demand-migrate-error-dialog-button-group {
  padding-top: 16px;
  display: flex;
  justify-content: space-between;
  margin: 15px;
}
.fb-on-demand-migrate-error-dialog-line {
  background-color: #181a1f;
  margin: 0px -9px 0px -9px;
  height: 3px;
}
.fb-on-demand-migrate-error-dialog-content {
  background-color: #161719;
  padding-left: 30px;
  padding-right: 30px;
  padding-top: 20px;
  padding-bottom: 20px;
  margin: 0px -9px -9px -9px;
  overflow: hidden;
  position: relative;
  text-align: center;
}
.fb-on-demand-migrate-error-dialog-header {
  color: #d9dbde;
  font-size: 1.75em;
  font-weight: bold;
  line-height: 1;
  margin-top: 14px;
  margin-bottom: 22px;
  letter-spacing: 0.3px;
  text-align: center;
}
.fb-on-demand-migrate-error-dialog-file-bug {
  display: flex;
  justify-content: space-between;
}
.fb-on-demand-migrate-error-dialog-bug-icon {
  position: absolute;
  margin: 2px 0px 0px 0px;
}
.fb-on-demand-migrate-error-dialog-bug-text {
  padding-left: 20px;
}
