css:
atom-panel.modal.fb-login-modal {
  width: 486px;
  /* Hosts the 450px webview nicely. */
}
.fb-login-modal-webview {
  margin: 10px auto;
  display: flex;
  height: 383px;
  /* The height required to crop the window with room for error messages. */
  width: 450px;
  /* The width required to crop the m.facebook.com login window. */
}
.fb-login-modal-logout {
  margin-right: 10px;
}
.fb-login-profile-pic {
  -webkit-filter: grayscale();
  position: absolute;
}
/* By trial and error I have determined how to cover the `person` icon in a center aligned way. */
.tool-bar-12px .fb-login-profile-pic {
  height: 16px;
  margin-left: -17px;
  margin-top: 4px;
  width: 16px;
}
.tool-bar-16px .fb-login-profile-pic {
  height: 20px;
  margin-left: -22px;
  margin-top: 6px;
  width: 20px;
}
.tool-bar-24px .fb-login-profile-pic {
  height: 34px;
  margin-left: -35px;
  margin-top: 6px;
  width: 34px;
}
.tool-bar-32px .fb-login-profile-pic {
  height: 50px;
  margin-left: -49px;
  margin-top: -1px;
  width: 50px;
}
.nuclide-fb-login-notification-icon {
  text-align: center;
}
.nuclide-fb-login-notification-icon:before {
  margin: auto;
}
.theme-one-dark-ui .fb-login-token-dialog-wrapper a {
  color: #6494ed;
}
.theme-one-light-ui .fb-login-token-dialog-wrapper a {
  color: #1492ff;
}
.theme-atom-dark-ui .fb-login-token-dialog-wrapper a,
.theme-atom-light-ui .fb-login-token-dialog-wrapper a {
  color: #5293d8;
}
.fb-login-token-dialog-wrapper {
  text-align: center;
}
.fb-login-token-dialog-status {
  font-size: 1.5em;
  font-weight: bold;
  margin: 10px 5px;
}
.fb-login-token-dialog-profile-wrapper {
  display: inline-block;
  text-align: left;
  height: 50px;
  margin: 10px;
}
.fb-login-token-dialog-profile-pic {
  display: inline-block;
  height: 50px;
  width: 50px;
}
.fb-login-token-dialog-profile-info {
  display: inline-block;
  margin-left: 5px;
  height: 50px;
}
.fb-login-token-dialog-profile-info-inner {
  display: inline-block;
  vertical-align: middle;
}
.fb-login-token-dialog-profile-info-inner:before {
  height: 50px;
}
.fb-login-token-dialog-name {
  font-size: 1.1em;
  font-weight: bold;
}
.fb-login-token-dialog-unixname {
  font-size: 0.9em;
  font-family: Menlo, Monaco, Consolas, monospace;
}
.fb-login-token-dialog-happy {
  color: #73c990;
  font-weight: bold;
  margin: 10px;
}
.fb-login-token-dialog-error {
  color: red;
  font-weight: bold;
  margin: 10px;
}
.fb-login-token-dialog-bottom-row {
  align-items: center;
  display: flex;
  justify-content: space-between;
  margin: 5px;
}
/* needs to beat atom-panel.modal atom-text-editor[mini] */
.fb-login-token-dialog-wrapper .fb-login-token-dialog-bottom-row .fb-login-token-dialog-input {
  flex-grow: 1;
  margin-bottom: 0;
}
.fb-login-token-dialog-spacer {
  width: 1em;
}
.fb-login-token-dialog-button-wrapper {
  display: inline-block;
  margin: 10px 0;
}
.fb-login-token-dialog-explanation {
  margin: 20px 30px;
}
.fb-login-token-dialog-input {
  text-align: left;
}
