css:
.status-bar .componentscript-toolbar-container {
  position: relative;
  top: -1px;
  /**
     status-bar does all kinds of nasty stuff to font size etc; attempt to match the default values
     for DropdownButton, as it would appear in a normal container that doesn't do that
     */
}
.status-bar .componentscript-toolbar-container .icon::before {
  width: initial;
  height: initial;
  font-size: 11px;
  top: 0;
}
.status-bar .componentscript-toolbar-container .icon {
  /** Dude, status-bar whyyyyy would you add margin to ALL icons?? */
  margin-right: 0 !important;
}
.componentscript-modal {
  cursor: default;
  -webkit-user-select: none;
}
.sample-usage {
  border-color: #eee;
  border-width: 0 1px 1px 1px;
  border-style: solid;
}
.sample-usage .instructions {
  padding: 12px;
}
.code-box {
  overflow-x: scroll;
  cursor: pointer;
}
.code-box pre {
  -webkit-user-select: none;
}
.componentscript-option-table {
  display: flex;
  flex-direction: column;
}
.componentscript-option-table .option-row {
  padding: 4px 4px 4px 24px;
  position: relative;
  font-weight: 500;
}
.componentscript-option-table .option-row .option-icon {
  position: absolute;
  left: 0;
}
.componentscript-option-table .error-row {
  color: #c00;
}
.componentscript-option-table .result-row {
  color: #17ca65;
}
.componentscript-template-radio-list .nuclide-ui-radiogroup-div {
  display: flex;
  margin-bottom: 5px;
}
.componentscript-template-radio-list .nuclide-ui-radiogroup-div input {
  flex-shrink: 0;
}
.componentscript-template-radio-list .nuclide-ui-radiogroup-div input,
.componentscript-template-radio-list .nuclide-ui-radiogroup-div label {
  cursor: pointer;
}
.componentscript-file-preview {
  padding: 4px 4px 4px 6px;
  border-radius: 2px;
  border: 1px solid #eee;
}
