css:
.nuclide-diff-view-padded {
  margin-left: 17px;
}
.nuclide-diff-view-editor-loading {
  pointer-events: none;
  opacity: 0.5;
}
.nuclide-diff-view-read-only-editor {
  opacity: 0.8;
}
.nuclide-diff-view-pane-loading-indicator {
  align-items: center;
  display: flex;
  height: 100%;
  justify-content: center;
  position: absolute;
  top: 0;
  width: 100%;
}
.nuclide-diff-timeline {
  z-index: 3;
  border-top: 1px solid #111314;
  width: 100%;
  color: #a3a8ae;
}
.nuclide-diff-timeline .text-monospace {
  font-family: Menlo, Consolas, monospace;
}
.nuclide-diff-timeline .revision {
  position: relative;
  cursor: pointer;
}
.nuclide-diff-timeline .revision-timeline-wrap {
  height: 100%;
  overflow: auto;
}
.nuclide-diff-timeline .revision-selector {
  width: 100%;
  white-space: nowrap;
  display: flex;
}
.nuclide-diff-timeline .revisions {
  display: flex;
  flex-direction: column;
  position: relative;
}
.nuclide-diff-timeline .revision:hover .revision-label {
  font-weight: bold;
}
.nuclide-diff-timeline .revision:hover .revision-bubble {
  background-color: #6494ed;
  box-shadow: 0 0 0 2px #6494ed;
}
.nuclide-diff-timeline .revision:hover .revision-bubble--uncommitted {
  background-color: #e2c08d;
  box-shadow: 0 0 0 2px #e2c08d;
}
.nuclide-diff-timeline .nuclide-diff-rev-side-button {
  margin-left: 0.4em;
}
.nuclide-diff-timeline .revision-bubble {
  background-color: #6494ed;
  border-radius: 10px;
  float: left;
  height: 10px;
  width: 10px;
  margin: 4px;
}
.nuclide-diff-timeline .revision-bubble::after {
  bottom: -8px;
  /* Pull into the revision bubble below */
  content: " ";
  display: block;
  width: 1px;
  background-color: #6494ed;
  margin-left: 5px;
  position: absolute;
  top: 5px;
  z-index: -1;
}
.nuclide-diff-timeline .revision-bubble--uncommitted {
  background-color: #e2c08d;
}
.nuclide-diff-timeline .selected-revision-inrange .revision-bubble:after {
  background: #6494ed;
  content: " ";
  margin-left: 1px;
  margin-right: 1px;
  width: 8px;
}
.nuclide-diff-timeline .revision-bubble--uncommitted:after {
  background: linear-gradient(to bottom, #e2c08d, #6494ed);
}
.nuclide-diff-timeline .selected-revision-last .revision-bubble::after {
  display: none;
}
.nuclide-diff-timeline .revision-label {
  margin-bottom: 12px;
  margin-left: 24px;
}
.nuclide-diff-timeline .selected-revision-start,
.nuclide-diff-timeline .selected-revision-end {
  font-weight: bold;
}
.nuclide-diff-timeline .selected-revision-inrange .revision-label,
.nuclide-diff-timeline .selected-revision-end .revision-label {
  color: #6494ed;
}
.nuclide-diff-timeline .selected-revision-start .revision-label {
  color: #e2c08d;
}
.nuclide-diff-timeline .selected-revision-start .revision-bubble {
  box-shadow: 0 0 0 4px rgba(226, 192, 141, 0.25);
}
.nuclide-diff-timeline .selected-revision-end .revision-bubble {
  box-shadow: 0 0 0 4px rgba(100, 148, 237, 0.25);
}
.diff-view-count.tool-bar-btn::after {
  content: attr(data-count);
  font-size: 0.8em;
  margin-top: 13px;
  position: absolute;
  text-shadow: 0 0px 2px #111314;
}
.diff-view-count.tool-bar-btn.max-count::after {
  margin-left: -4px;
}
.diff-view-count.tool-bar-btn.positive-count {
  color: #e2c08d;
}
.diff-view-count.tool-bar-btn.positive-count:hover {
  color: #e2c08d;
}
.nuclide-diff-view-block-offset {
  width: 100%;
  background: repeating-linear-gradient(-45deg, #242729, #242729, 5px, transparent 5px, transparent 10px);
}
/* BEGIN Keep in sync with nuclide-ui/styles/fb-FileChanges: */
atom-text-editor .diff-view-insert .region {
  border-left-style: solid;
  border-left-color: #73c990;
  border-left-width: 1px;
  background-color: rgba(115, 201, 144, 0.1);
}
atom-text-editor .diff-view-delete .region {
  border-left-style: solid;
  border-left-color: #ff6347;
  border-left-width: 1px;
  background-color: rgba(255, 99, 71, 0.1);
}
/* END Keep in sync with nuclide-ui/styles/fb-FileChanges: */
.nuclide-diff-view-split-navigation-gutter {
  position: absolute;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
}
.nuclide-diff-view-navigation-bar {
  height: 100%;
  width: 100%;
}
.publish-toolbar-wrapper .nuclide-ui-toolbar__left {
  flex: 2;
}
.nuclide-diff-view-navigation-target {
  position: absolute;
  width: 100%;
  cursor: pointer;
  text-align: center;
}
.nuclide-diff-view-navigation-target.syntax--added {
  background-color: #2ba143;
}
.nuclide-diff-view-navigation-target.syntax--removed {
  background-color: #d13b2e;
}
.nuclide-diff-view-navigation-target.syntax--modified {
  background-color: #ad7c0b;
}
.nuclide-diff-view-navigation-target.icon {
  z-index: 1;
}
.nuclide-diff-view-navigation-target.icon::before {
  margin-right: 0;
}
/*
 * Animation originally taken from [atom/settings-view][1] to match its installing/uninstalling
 * buttons.
 *
 * [1] https://github.com/atom/settings-view/blob/cc409bacf1c44dbd7d27626809b11ea7a01639a0/styles/package-card.less#L296
 */
@keyframes btn-progress {
  100% {
    background-position: -10px 0px;
  }
}
.nuclide-diff-view-navigator-root {
  display: flex;
  width: 100%;
}
.nuclide-diff-view-navigator-file-changes-container {
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
}
.nuclide-diff-view-tree {
  overflow: scroll;
  border-top: 1px solid #111314;
}
.nuclide-diff-view-navigator-timeline-container {
  overflow: scroll;
  display: flex;
  height: 100%;
}
.nuclide-diff-view-direction {
  position: absolute;
  margin-left: 5px;
  margin-top: -3px;
}
.nuclide-diff-view-file-change-spinner {
  position: absolute;
  margin-left: 5px;
  margin-top: -2px;
}
